graphql.query:
query {
  health {
    status
    service
    gitea {
      status
      message
    }
    timestamp
  }
}

query {
  repositoryStats {
    totalRepositories
    totalForks
    totalStars
    totalSize
  }
}

query ListRepositories($limit: Int, $offset: Int) {
  listRepositories(limit: $limit, offset: $offset) {
    repositories {
      id
      name
      fullName
      description
      private
      fork
      size
      starsCount
      forksCount
      createdAt
      updatedAt
      owner {
        login
        fullName
      }
    }
    total
    limit
    offset
  }
}

query SearchRepositories($query: String, $limit: Int, $offset: Int) {
  searchRepositories(query: $query, limit: $limit, offset: $offset) {
    repositories {
      id
      name
      fullName
      description
      starsCount
    }
    total
  }
}

query MyRepositories($limit: Int, $offset: Int) {
  myRepositories(limit: $limit, offset: $offset) {
    repositories {
      id
      name
      fullName
      description
      private
      starsCount
    }
    total
  }
}

mutation CreateRepository($name: String!, $description: String, $private: Boolean, $autoInit: Boolean) {
  createRepository(name: $name, description: $description, private: $private, autoInit: $autoInit) {
    id
    name
    fullName
    description
    private
    htmlUrl
    cloneUrl
    defaultBranch
  }
}

