mutation ForkRepository($owner: String!, $repo: String!, $organization: String) {
  forkRepository(owner: $owner, repo: $repo, organization: $organization) {
    id
    name
    fullName
    fork
  }
}

mutation MigrateRepository($cloneAddr: String!, $repoName: String!, $private: Boolean, $mirror: Boolean, $service: String) {
  migrateRepository(cloneAddr: $cloneAddr, repoName: $repoName, private: $private, mirror: $mirror, service: $service) {
    id
    name
    fullName
    cloneUrl
  }
}

mutation DeleteRepository($owner: String!, $name: String!) {
  deleteRepository(owner: $owner, name: $name)
}

query ListBranches($owner: String!, $repo: String!, $page: Int, $limit: Int) {
  listBranches(owner: $owner, repo: $repo, page: $page, limit: $limit) {
    name
    commit {
      id
      message
      author {
        name
        email
      }
    }
    protected
  }
}

query GetBranch($owner: String!, $repo: String!, $branch: String!) {
  getBranch(owner: $owner, repo: $repo, branch: $branch) {
    name
    commit {
      id
      message
      author {
        name
        email
      }
      committer {
        name
        email
      }
    }
    protected
  }
}

mutation CreateBranch($owner: String!, $repo: String!, $branchName: String!, $oldBranchName: String!) {
  createBranch(owner: $owner, repo: $repo, branchName: $branchName, oldBranchName: $oldBranchName) {
    name
    commit {
      id
      message
    }
  }
}

mutation DeleteBranch($owner: String!, $repo: String!, $branch: String!) {
