}

mutation SyncGroupToTeam($groupCn: String!, $orgName: String!, $teamName: String, $permission: String!) {
  syncGroupToTeam(groupCn: $groupCn, orgName: $orgName, teamName: $teamName, permission: $permission) {
    team {
      id
      name
      description
    }
    membersAdded
    membersFailed
    repositoriesAdded
    repositoriesFailed
    errors
  }
}
